package practice.neetCode150.part3SlidingWindow.medium;

import java.util.*;

public class permutationInString {

    public static void main(String args[]) {

        System.out.println(checkInclusion("ab", "eidbaoooab"));
        System.out.println(checkInclusion("adc", "dcda"));
        System.out.println(
                checkInclusion("trinitrophenylmethylnitramine", "dinitrophenylhydrazinetrinitrophenylmethylnitramine"));
        System.out.println(checkInclusion(
                "",
                ""));

    }

    public static boolean checkInclusion(String s1, String s2) {

        HashMap<Character, Integer> map1 = new HashMap<>();

        for (char c : s1.toCharArray())
            map1.put(c, map1.getOrDefault(c, 0) + 1);

        int l = 0, r = 0;
        int have = 0, need = map1.size();
        HashMap<Character, Integer> map2 = new HashMap<>();

        // for (int r = 0; r < s2.length(); r++) {
        while (r < s2.length()) {

            char rc = s2.charAt(r);
            map2.put(rc, map2.getOrDefault(rc, 0) + 1);

            if (map1.containsKey(rc) && map1.get(rc).intValue() == map2.get(rc).intValue())
                have++;

            while (have == need) {

                if (r - l + 1 == s1.length())
                    return true;

                char lc = s2.charAt(l);
                map2.put(lc, map2.get(lc) - 1);

                if (map1.containsKey(lc) && map2.get(lc).intValue() < map1.get(lc).intValue())
                    have--;

                if (map2.get(lc) == 0)
                    map2.remove(lc);

                l++;

            }

            r++;

        }

        return false;

    }

}
